/* Dataset container */
.dataset-container {
    padding: 20px;
    background-color: #f9f9f9;
    border-radius: 10px;
    box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.1);
  }
  
  .dataset-box {
    margin-bottom: 20px;
    padding: 20px;
    background-color: #fff;
    border-radius: 10px;
    box-shadow: 0px 0px 5px rgba(0, 0, 0, 0.1);
  }
  
  .dataset-title {
    font-size: 24px;
    font-weight: bold;
    color: #333;
    margin-bottom: 15px;
  }
  
  .dataset-description {
    font-size: 16px;
    line-height: 1.5;
    color: #666;
    margin-bottom: 10px;
  }
  
  .dataset-modified {
    font-size: 14px;
    color: #999;
    margin-bottom: 5px;
  }
  
  .dataset-opd {
    font-size: 16px;
    font-weight: bold;
    color: #555;
  }
  
  /* Table styling */
  .dataset-table {
    width: 100%;
    border-collapse: collapse;
    margin-top: 20px;
  }
  
  .dataset-table th,
  .dataset-table td {
    padding: 10px;
    text-align: left;
    border: 1px solid #ddd;
  }
  
  .dataset-table th {
    background-color: #f1f1f1;
    font-weight: bold;
  }
  
  .dataset-table td {
    background-color: #fff;
  }
  
  .pagination {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-top: 20px;
  }
  
  .pagination-button {
    padding: 10px 15px;
    font-size: 14px;
    background-color: #007bff;
    color: white;
    border: none;
    border-radius: 5px;
    cursor: pointer;
    transition: background-color 0.3s;
  }
  
  .pagination-button:disabled {
    background-color: #ddd;
    cursor: not-allowed;
  }
  
  .pagination-button:hover:not(:disabled) {
    background-color: #0056b3;
  }
  
  .page-info {
    font-size: 14px;
    font-weight: bold;
    color: #333;
    margin: 0 10px;
  }
  
  .error-message {
    color: red;
    font-size: 14px;
    margin-top: 10px;
  }
  
  .loading-message {
    font-size: 16px;
    color: #555;
  }
