+/* Fade in animation */
+@keyframes fadeIn {
+  from {
+    opacity: 0;
+    transform: translateY(-10px);
+  }
+  to {
+    opacity: 1;
+    transform: translateY(0);
+  }
+}
+
+/* Form styles */
+.sektoral-form {
+  display: flex;
+  flex-wrap: nowrap; /* Mencegah wrap agar semua muat dalam satu baris */
+  align-items: center;
+  gap: 10px;
+  justify-content: flex-start;
+  margin-top: 10px;
+}
+
+/* Input styles */
+.sektoral-input {
+  padding: 8px;
+  border: 1px solid #ccc;
+  border-radius: 4px;
+  font-size: 14px;
+  box-sizing: border-box;
+}
+
+/* Specific width for smaller inputs (dari tahun & sampai tahun) */
+.sektoral-input.dari-tahun,
+.sektoral-input.sampai-tahun {
+  width: 40px; 
+}
+
+/* Specific width for larger inputs (pilih OPD & urusan) */
+.sektoral-input.pilih-opd,
+.sektoral-input.urusan {
+  width: 200px; 
+}
+
+/* Button styles */
+.search-button {
+  background-color: #007bff;
+  color: #fff;
+  padding: 8px 15px;
+  border: none;
+  border-radius: 4px;
+  cursor: pointer;
+  font-size: 16px;
+  transition: background-color 0.3s, transform 0.3s;
+  height: 44px;
+}
+
+.search-button:hover {
+  background-color: #0056b3;
+  transform: scale(1.05);
+}
+
+/* Error message styles */
+.error-message {
+  color: red;
+  margin-top: 10px;
+  text-align: center;
+}
+
+/* Result title styles */
+.result-title {
+  text-align: center;
+  font-size: 18px;
+  margin: 10px 0;
+  animation: slideIn 0.5s ease-in;
+  font-family: 'Arial', sans-serif;
+}
+
+/* Slide in animation */
+@keyframes slideIn {
+  from {
+    opacity: 0;
+    transform: translateY(-20px);
+  }
+  to {
+    opacity: 1;
+    transform: translateY(0);
+  }
+}
+
+/* Result table styles */
+.sektoral-table {
+  width: 100%;
+  border-collapse: collapse;
+  margin-top: 20px;
+}
+
+.sektoral-table th,
+.sektoral-table td {
+  border: 1px solid #ddd;
+  padding: 10px;
+  text-align: left;
+}
+
+.sektoral-table th:nth-child(1),
+.sektoral-table td:nth-child(1) {
+  width: 50px;
+}
+
+.sektoral-table th:nth-child(2),
+.sektoral-table td:nth-child(2) {
+  width: 250px;
+}
+
+.sektoral-table th:nth-child(3),
+.sektoral-table td:nth-child(3) {
+  width: 80px;
+}
+
+.sektoral-table th:nth-child(4),
+.sektoral-table td:nth-child(4) {
+  width: 150px;
+}
+
+.sektoral-table th {
+  background-color: #007bff;
+  color: white;
+  font-weight: bold;
+}
+
+/* Pagination styles */
+.pagination {
+  display: flex;
+  justify-content: space-between;
+  align-items: center;
+  margin-top: 20px;
+}
+
+.pagination-button {
+  padding: 10px 15px;
+  background-color: #007bff;
+  color: #fff;
+  border: none;
+  border-radius: 4px;
+  cursor: pointer;
+  transition: background-color 0.3s, transform 0.3s;
+}
+
+.pagination-button:hover {
+  background-color: #0056b3;
+  transform: scale(1.05);
+}
+
+.pagination-button:disabled {
+  background-color: #ccc;
+  cursor: not-allowed;
+}
+
+.page-info {
+  flex-grow: 1;
+  text-align: center;
+}

