+/* styles/Sektoral.css */
+.sektoral-container {
+    width: 100%;
+    max-width: 800px; /* Anda dapat menyesuaikan lebar ini */
+    margin: auto;
+    padding: 20px;
+    border: 1px solid #ccc;
